import { Component, OnInit } from '@angular/core';
import { LoadingController, ToastController } from '@ionic/angular';
import { Router } from '@angular/router';

@Component({
  selector: 'app-otps',
  templateUrl: './otps.page.html',
  styleUrls: ['./otps.page.scss'],
})
export class OtpsPage implements OnInit {

  otp: string = "";

  constructor(
    public loadingCtrl: LoadingController,
    public toastsCtrl: ToastController,
    private router: Router
  ) { }

  ngOnInit() {
  }

  setIpFocus() {
    for (let i = 1; i <= 6; i++){
      const element = document.getElementById("ip" + i);
      if (element !== null) {
        if ((this.otp.length + 1) === i) {
          element.style.background = "var(--ion-color-dark)";
        } else {
          element.style.background = "var(--ion-color-light)";
        }
      }
    }
  }
  

  clear(){
    this.otp = "";
    this.setIpFocus();
  }

  back(){
    this.otp = this.otp.slice(0, -1);
    this.setIpFocus();
  }

  set(number: string){
    this.otp += number;
    this.setIpFocus();

    if(this.otp.length == 6){
      this.presentLoading();
      this.checkOTP();
    }
  }

  async presentLoading() {
    const loading = await this.loadingCtrl.create({
      message: 'Verifying OTP...',
      spinner: "circular"
    });
    await loading.present();
  }

  async presentToast(message: string, color: string) {
    const toast = await this.toastsCtrl.create({
      message: message,
      color: color,
      duration: 1000,
      position: "middle",
    });
    toast.present();
  }

  checkOTP() {
    setTimeout(() => {
      this.loadingCtrl.dismiss();

      if(this.otp == "123456") {
        this.presentToast("OTP Verified", "success");
        this.router.navigateByUrl('/login');
      }
      else {
        this.presentToast("Invalid OTP", "danger");
      }
    }, 2000);
  }

}
<ion-content class="ion-padding ion-text-center">
  <h3 style="margin-top: 100px">
    Verification
  </h3>

  <ion-grid class="inputs">
    <ion-row>
      <ion-col size="2">
        <input type="text" value="{{otp.substring(0,1)}}" readonly id="ip1">
      </ion-col>
      <ion-col size="2">
        <input type="text" value="{{otp.substring(1,2)}}" readonly id="ip2">
      </ion-col>
      <ion-col size="2">
        <input type="text" value="{{otp.substring(2,3)}}" readonly id="ip3">
      </ion-col>
      <ion-col size="2">
        <input type="text" value="{{otp.substring(3,4)}}" readonly id="ip4">
      </ion-col>
      <ion-col size="2">
        <input type="text" value="{{otp.substring(4,5)}}" readonly id="ip5">
      </ion-col>
      <ion-col size="2">
        <input type="text" value="{{otp.substring(5,6)}}" readonly id="ip6">
      </ion-col>
    </ion-row>
  </ion-grid>

  <ion-note>
    Didn't receive OTP?
    <ion-text color="primary">
      resend OTP
    </ion-text>
  </ion-note>

  <ion-grid class="buttons">
    <ion-row>
      <ion-col size="4">
        <button (click)="set('1')">1</button>
      </ion-col>
      <ion-col size="4">
        <button (click)="set('2')">2</button>
      </ion-col>
      <ion-col size="4">
        <button (click)="set('3')">3</button>
      </ion-col>
      <ion-col size="4">
        <button (click)="set('4')">4</button>
      </ion-col>
      <ion-col size="4">
        <button (click)="set('5')">5</button>
      </ion-col>
      <ion-col size="4">
        <button (click)="set('6')">6</button>
      </ion-col>
      <ion-col size="4">
        <button (click)="set('7')">7</button>
      </ion-col>
      <ion-col size="4">
        <button (click)="set('8')">8</button>
      </ion-col>
      <ion-col size="4">
        <button (click)="set('9')">9</button>
      </ion-col>
      <ion-col size="4">
        <button (click)="clear()">Clear</button>
      </ion-col>
      <ion-col size="4">
        <button (click)="set('0')">0</button>
      </ion-col>
      <ion-col size="4">
        <button (click)="back()">Back</button>
      </ion-col>
    </ion-row>
  </ion-grid>
</ion-content>
